import React, { useState } from 'react'
import '../App.css'
import logo from "../assets/logo.png"
import { menuItem } from './ConstantList'

export default function Sidebar() {
    function check(list){
        if(list !== undefined){
            item.sub_menu.map((item)=>
            <li>
                <a>{item.name}</a>
            </li>
            )
        }
    }
    const [inactive, setInactive] = useState(false)
  return (
    <div className={`sidebar ${inactive ? "inactive" : ""}`}>
        <div className='top-section'>
            <div className='logo'>
                <img src={logo} />
            </div>
            <div onClick={()=> setInactive(!inactive)} className='toggle-button'>
                <i className={`bi bi-arrow-${inactive ? "right": "left"}-square-fill`}></i>
            </div>
        </div>
        <div className='main-menu'>
            <ul>
                {menuItem.map((item)=>
                <li>
                    <a className='menu-item'>
                        <span className='menu-icon'>
                            <i className={item.icon}> </i>
                        </span>
                        {item.name}
                    </a>
                    <ul>{check(item.sub_menu)}</ul>
                </li>
                )}
            </ul>

        </div>
    </div>
  )
}
